  // Convert to lowercase for case-insensitive matching
  const lowerTitle = jobTitle.toLowerCase();

  // Filter out Backend positions
  if (lowerTitle.includes('backend')) {
    console.log(`Filtered out backend position: ${jobTitle}`);
    return; // Skip this item
  }

  // Filter out MERN developer positions
  if (lowerTitle.includes('mern')) {
    console.log(`Filtered out MERN position: ${jobTitle}`);
    return; // Skip this item
  }

  // Normalize LinkedIn URLs by removing query parameters
  // LinkedIn job ID is in the path (e.g., /jobs/view/12345)
  let normalizedUrl = jobUrl;
  if (jobUrl && jobUrl.includes('linkedin.com/jobs')) {
    // Remove everything after the ? (query parameters like refId, trackingId, etc.)
    normalizedUrl = jobUrl.split('?')[0];
  }

  // Only keep the first occurrence of each normalized URL
  if (!uniqueJobs.has(normalizedUrl)) {
    uniqueJobs.set(normalizedUrl, item);
  }
});

