// Use a Map to track unique jobs by URL
const uniqueJobs = new Map();

items.forEach(item => {
  const jobUrl = item.json.jobUrl;

  // Only keep the first occurrence of each URL
  if (!uniqueJobs.has(jobUrl)) {
    uniqueJobs.set(jobUrl, item);
  }
});

// Convert back to array
